		int rangeMin=100; 
		int rangeMax=400;
		int minCommission=20;
		int base=0;
		double midMin=25.0;
		double midMax=40.0; 
		double rate1=0.15; 
		double rate2=0.12; 
		 
        double commissionRate=0.00;
        double calculatedCommission = 0.0;
        double basePrice=0.0;
        double salePrice = price.getSalePrice();
        
        if(salePrice>=rangeMin&&salePrice<rangeMax){
            commissionRate=rate1;
            calculatedCommission = salePrice * commissionRate;
            if(calculatedCommission>=midMax){
            calculatedCommission=midMax;
            }
            if(calculatedCommission<=midMin){
            calculatedCommission=midMin;
            } 
                              			   
		}else if(salePrice>=rangeMax){
		    commissionRate=rate2;
		    calculatedCommission = salePrice * commissionRate;				    
		}else if(salePrice==base){
		    calculatedCommission = 0;
		    commissionRate=0;
		    
		}else{
			calculatedCommission = minCommission;
		    commissionRate=minCommission/salePrice;
		    java.text.DecimalFormat  df=new java.text.DecimalFormat("#.##");
		    commissionRate=Double.parseDouble(df.format(commissionRate)); 
		}
		price.setBasePrice(salePrice-calculatedCommission);
        price.setCalculatedCommission(calculatedCommission);
        price.setCommissionRate(commissionRate);
